import { Component,OnInit } from '@angular/core';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit{
  

  title = 'DataBindingDemo';
  text="";  
  constructor(){}
  
  animalImage = '';  
  ngOnInit(){
    
  }
  OnUpdateServerName(event: Event) {  
    this.animalImage = (<HTMLInputElement>event.target).value;  
    if(this.animalImage.toLowerCase()=="lion"){
      this.animalImage="data:image/jpeg;base64,";
    }else if(this.animalImage.toLowerCase()=="tiger"){
      this.animalImage="data:image/jpeg;base64,";
    }else if(this.animalImage.toLowerCase()=="rabbit"){
      this.animalImage="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTN9UZi3p3JRotSoGA73GFnSCJRywhxKRVTpA&usqp=CAU";
  }  else{
    this.animalImage="Please enter only lion / tiger/ rabbit"
  }
  
  }
  
}
